    private static int rangeSum(int[] nums, int n, int left, int right) {
        int allSubArrayLength = (n + 1) * n / 2;
        if (left < 1 || right > allSubArrayLength || left > right) {
            return -999999;
        }
        int[] allSubArray = new int[allSubArrayLength];
        int startIndex = 0;
        for (int i = 0; i < n; i++) {
            allSubArray[startIndex++] = nums[i];
            int countStore = nums[i];
            for (int j = i + 1; j < n; j++) {
                countStore += nums[j];
                allSubArray[startIndex++] = countStore;
            }
        }
        Arrays.sort(allSubArray);
        int sum = 0;
        int mod = 1000000007;
        for (int i = left - 1; i <= right - 1; i++) {
            sum = (sum + allSubArray[i]) % mod;
        }
        return sum;
    }

